import React,  {useState, useEffect} from 'react';
import './App.css';
import DashBoard from './components/DashBoard/dashboard';
import Login from './components/LoginSignup/login';
import Signup from './components/LoginSignup/signup';
import { createBrowserRouter, Router, Routes, Navigate } from 'react-router-dom';
import axios from 'axios';



/* functions to check if user is loged in or not */
const checkLoginStatus = async () => {
    const logged = await axios.get('/user-data');

    if (logged.status === 200){
      return true;}
    else{
      return false;}
};

const ProtectRoute = ({element: Element}) => {
  const [logged , setLogged] = useState(null);

  useEffect (() => { 
    const login_verify = async () =>
    {
      const bool = await checkLoginStatus();
      setLogged(bool);
    };
    login_verify();}, [])

    if (logged === null) {
      return <p>Loading...</p>;
    }

    return logged ? <Element/> : <Navigate to="/login" />;
};

const router = createBrowserRouter([
  {
    path: "/",
    element: <DashBoard/>,
  },
  {
    path: "/login",
    element: <Login />,
  },
  {
    path: "/signup",
    element: <Signup />,
  },
  // {
  //   path: "/calendar",
  //   element: <Calendar />,
  // },
  // {
  //   path: "/create",
  //   element: <CreationForm />,
  // },
]);
function App() {

  return (
  <Router>
  <Routes>
    <Route path="/login" element={<Login />} />
    <Route path="/signup" element={<Signup />} />
    <Route path="/" element={<ProtectedRoute element={DashBoard} />} />

  </Routes>
  </Router>

    // return (
    //   <div>
    //     <RouterProvider router={router}/>
    //   </div>

    );  
}

export default App;
